def mpow(a, e, mod):
    if e == 0:
        return 1
        
    if a == 1:
        return 0
        
    hpow = mpow(a, e//2, mod)
    ans = (hpow * hpow)%mod
    
    if e%2:
        return (ans * a)%mod
        
    return ans

class Solution:
    # @param A : integer
    # @param B : list of integers
    # @return an integer
    def solve(self, A, B):
        cnks = []
        
        cs = [False for i in range(1, A+1)]
        
        for vis in B:
            cs[vis-1] = True
         
        if not cs[0]:
            cnks.append(1)
            
        
        for i, st in enumerate(cs):
            if i == 0:
                continue
            
            if (not cs[i-1]) and (not st):
                cnks[-1] += 1
                
            if (not cs[i-1]) and st:
                continue
            
            if (cs[i-1]) and (not st):
                cnks.append(1)
                
            if (cs[i-1]) and st:
                continue
         
        print(cnks)   
        nfact = mfacts[sum(cnks)]
        dfact = 1
        
        for cnk in cnks:
            dfact *= mfacts[cnk]
            dfact %= 1000000007
            
        dinv = mpow(dfact, 1000000005, 1000000007)
        fact = (nfact * dinv)%1000000007
        n = len(cnks)
        
        for i, cnk in enumerate(cnks):
            if (i == 0) and (not cs[0]):
                continue
            
            if (i == n-1) and (not cs[-1]):
                continue
            
            w = mpow(2, cnk-1, 1000000007)
            fact *= w
            fact %= 1000000007
            
        return fact
